import { createStore } from "redux";

const counterReducer = (state = { counter: 0, shown: true }, action) => {
  if (action.type === "increment") {
    return { counter: state.counter + 1, shown: state.shown };
  }
  if (action.type === "incrementBy5") {
    return { counter: state.counter + action.amount, shown: state.shown };
  }
  if (action.type === "decrement") {
    return { counter: state.counter - 1, shown: state.shown };
  }
  if (action.type === 'toggleCounter') {
    return { counter: state.counter, shown: !state.shown }
  }
  return state;
};

const store = createStore(counterReducer);

export default store;
